import 'package:crafty_bay_ecommerce/modules/view%20screens/home%20screen/view/new_product_screen.dart';import 'package:crafty_bay_ecommerce/modules/view%20screens/home%20screen/view/popular_product_screen.dart';import 'package:crafty_bay_ecommerce/modules/view%20screens/home%20screen/view/special_product_screen.dart';import '../../../../utils/export.dart';import '../../bottom nav/controller/bottom_nav_controller.dart';import '../component/all_categories_list_builder.dart';import '../component/categories_button.dart';import '../component/dots_indicator.dart';import '../component/list_product_by_remark_builder.dart';import '../controller/home_screen_controller.dart';class HomeScreen extends StatelessWidget {  HomeScreen({super.key});  final controller = Get.put(HomeScreenController());  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: buildAppBar(context),      body: RefreshIndicator(        onRefresh: controller.initializeMethod,        child: ListView(          shrinkWrap: true,          children: [            const SizedBox(height: kTextSize),            // CustomTextField widget for searching products.            CustomTextField(              controller: controller.searchController,              prefixIcon: const Icon(Icons.search),              hintText: 'Search Products',              onChanged: (query) {},              label: 'Search',            ),            // CarouselSliderBuilder widget for displaying product sliders.            CarouselSliderBuilder(controller.listProductSlider),            // DotsIndicatorWidget for indicating the position in the slider.            DotsIndicatorWidget(controller.listProductSlider),            // CategoriesButton widget for displaying a category button (e.g., 'All Categories').            CategoriesButton(                onTap: () {                  Get.find<BottomNavController>().goToCategoryScreen();                },                categoryName: 'All Categories'),            // AllCategoriesListBuilder for displaying a list of categories.            AllCategoriesListBuilder(controller.categoryList),            // CategoriesButton for 'Popular' category.            CategoriesButton(                onTap: () {                  Get.to(() => PopularProductScreen());                },                categoryName: 'Popular'),            // ListProductByRemarkBuilder for displaying products in the 'Popular' category.            ListProductByRemarkBuilder(controller.listProductByRemarkPopular),            // CategoriesButton for 'Special' category.            CategoriesButton(                onTap: () {                  Get.to(() => SpecialProductScreen());                },                categoryName: 'Special'),            // ListProductByRemarkBuilder for displaying products in the 'Special' category.            ListProductByRemarkBuilder(controller.listProductByRemarkSpecial),            // CategoriesButton for 'New' category.            CategoriesButton(                onTap: () {                  Get.to(() => NewProductScreen());                },                categoryName: 'New'),            // ListProductByRemarkBuilder for displaying products in the 'New' category.            ListProductByRemarkBuilder(controller.listProductByRemarkNew),          ],        ),      ),    );  }}