import 'dart:convert';import '../base_url.dart';import 'package:http/http.dart' as http;Future fetchCategoryListRequest() async {  try {    final Uri uri = Uri.parse(categoryListUrl);    final http.Response response = await http.get(uri);    if (response.statusCode == 200) {      final Map<String, dynamic> responseBody = jsonDecode(response.body);      if (responseBody['msg'] == 'success') {        final List<Map<String, dynamic>> data =            List<Map<String, dynamic>>.from(responseBody['data']);        return data;      } else {        throw Exception('Error has occurred: ${responseBody['msg']}');      }    } else {      throw Exception('Error has occurred: ${response.statusCode}');    }  } catch (e) {    throw Exception('An error occurred :$e');  }}